    }

    handlePause = e => {
        e.preventDefault();
        this.setState({
            playing: false
        })
        return 
            bBeat.stop();


    }

    handlePlay = e => {
        e.preventDefault();
        this.setState({
            playing: true
        })

        var context = new AudioContext()
        var bBeat = new BinauralBeat(context);
        var volume = context.createGain();
        bBeat.connect(volume)
        volume.connect(context.destination)
        volume.gain.value = .8
        bBeat.start()
    }

    render() {
        const feelings = this.state.feelings.map((item, key) =>
            <div key={item.name} className="feelingContainer">
                <div className="chakra">
                    <img src={require(`../img/feelings/${item.color}.png`)} />
                </div>
                <div className={`${item.color}`}>
                    <b className="frequencyText">{item.frequency_range}</b>
                    <br />
                    <b> {item.title} </b>
                    <br />
                    <div className="details">
                        <b> Shift from: </b>
                        <p> {item.shift_from} </p>
                        <b> Shift to: </b>
                        <p> {item.shift_to} </p>
                    </div>
                    <div>
                    </div>
                    {item.feelings.map((feeling, key) => (
                        <button key={feeling} value={feeling} className={`btn btn-success ${item.color}`} onClick={e => this.setFeeling(e.target.value, item.frequency)}>
                            {feeling}
                        </button>
                    ))}
                </div>
            </div>
        );

        const boost = this.state.boosts.map((item, key) =>
            <div key={item.id} className="feelingContainer">
                <div className="chakra">
                    <img src={require(`../img/boosts/${item.name}.png`)} />
                </div>
                <div className={`${item.logo}`}>
                    <b className="frequencyText">{item.frequency}</b>

                    <div className="details">
                        <b> Shift from: </b>
                        <p> {item.shift_from} </p>
                        <b> Shift to: </b>
                        <p> {item.shift_to} </p>
                    </div>
                </div>
                <button key={item.name} value={item.name} className={`btn btn-success ${item.logo}`} onClick={e => this.setBoost(e.target.value, item.id)}>
                    {item.name}
                </button>

            </div>
        )

        const activity = this.state.activities.map((item, key) =>
            <button key={item.name} value={item.name} className={'btn btn-success activity'} onClick={e => this.setActivity(e.target.value, item.frequencies)}>
                {item.name}
            </button>
        );

        const playable = ((this.state.feeling !== "") &&
            (this.state.boost !== "") &&
            (this.state.activity !== ""))

        let player;
        if (playable) {
            if (this.state.playing) {
                player = <button className="glyphicon glyphicon-pause" onClick={this.handlePause}></button>
            } else {
                player = <button className="glyphicon glyphicon-play-circle" onClick={this.handlePlay}></button>
            }
        } else {
            player = <button className="glyphicon glyphicon-play-circle" disabled></button>
        }

        return (
            <div>
                {console.log(playable)}
                <Header />
                <div>
                    <div className="div1">
                        <div className="text">
                            I WANT TO FEEL
                            <br />
                            <div className="selectText">
                                <b>
                                    {this.state.feeling ? this.state.feeling : "Select Mood"}
                                </b>
                            </div>
                            <div className="overlay">
                                <div className="selector">
                                    Choose one:
                                    <br />
                                    {feelings}
                                </div>
                            </div>
                        </div>
                    </div>
                    <div className="div2">
                        <div className="text">
                            AND BOOST MY
                            <br />
                            <div className="selectText">
                                <b>
                                    {this.state.boost ? this.state.boost : "Select Mood"}
                                </b>
                            </div>
                            <div className="overlay">
                                <div className="selector">
                                    Choose one:
                                    <br />
                                    {boost}
                                </div>
                            </div>
                        </div>
                    </div>
                    <div className="div3">
                        <div className="text">
                            WHILE I
                            <br />
                            <div className="selectText">
                                <b>
                                    {this.state.activity ? this.state.activity : "Select Mood"}
                                </b>
                            </div>
                            <div className="overlay">
                                <div className="selector">
                                    Choose one:
                                    <br />
                                    {activity}
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
                <div className="bg-light">
                    {player}
                </div>
            </div>
        )
    }
}

export default Webapp;
